import setComponentProps from './component/setComponentProps'
import createComponent from './component/createComponent'

export function renderComponent(component) {

  let base;

  const renderer = component.render();

  if (component.base && component.componentWillUpdate) {
    component.componentWillUpdate();
  }

  base = _render(renderer);

  if (component.base) {
    if (component.componentDidUpdate) component.componentDidUpdate();
  } else if (component.componentDidMount) {
    component.componentDidMount();
  }

  if (component.base && component.base.parentNode) {
    component.base.parentNode.replaceChild(base, component.base);
  }

  component.base = base;
  base._component = component;

}

export function render(vnode, container) {
  return container.appendChild(_render(vnode));
}

export function setAttribute(dom, name, value) {
  // 如果属性名是className，则改回class
  if (name === 'className') name = 'class';

  // 如果属性名是onXXX，则是一个事件监听方法
  if (/on\w+/.test(name)) {
    name = name.toLowerCase();
    dom[name] = value || '';
    // 如果属性名是style，则更新style对象
  } else if (name === 'style') {
    if (!value || typeof value === 'string') {
      dom.style.cssText = value || '';
    } else if (value && typeof value === 'object') {
      for (let name in value) {
        // 可以通过style={ width: 20 }这种形式来设置样式，可以省略掉单位px
        dom.style[name] = typeof value[name] === 'number' ? value[name] + 'px' : value[name];
      }
    }
    // 普通属性则直接更新属性
  } else {
    if (name in dom) {
      dom[name] = value || '';
    }
    if (value) {
      dom.setAttribute(name, value);
    } else {
      dom.removeAttribute(name, value);
    }
  }
}

export function _render(vNode) {
  // 当vvNode为字符串时，渲染结果是一段文本
  if (typeof vNode === 'string' || typeof vNode === 'number') {
    const textNode = document.createTextNode(vNode);
    return textNode;
  }

  if (typeof vNode.tag === 'function') {
    let component = createComponent(vNode.tag, vNode.attrs);
    component = setComponentProps(component, vNode.attrs);
    renderComponent(component);

    return component.base;
  }

  const dom = document.createElement(vNode.tag);

  if (vNode.attrs) {
    Object.keys(vNode.attrs).forEach(key => {
      // 当属性名为className时，改回class
      if (key === 'className') {
        key = 'class';
      }
      setAttribute(dom, key, vNode.attrs[key]);
    });
  }

  vNode.children.forEach(child => render(child, dom)); // 递归渲染子节点
  return dom; // 将渲染结果挂载到真正的DOM上
}
